import React from "react";
import pokemons from '../data';

class Pokemon extends React.Component {
  render() {

    const { name, type, averageWeight, image } = pokemons[0];
    return (
      <div>
        <span>{name}</span>
        <span>{type}</span>
        <span>{`${averageWeight.value} ${averageWeight.measurementUnit}`}</span>
        <img src={image} />
      </div>
    );
  }
}
